mapper.select:
SELECT *
FROM keywords

     domain_id = #{domainId} 
     id LIKE CONCAT(CONCAT('%', #{keyword}), '%') 
     name LIKE CONCAT(CONCAT('%', #{keyword}), '%') 

LIMIT #{offset}, #{perPage}

mapper.update:
UPDATE keywords
set name = #{name}
where id = #{id} and domain_id = #{domainId}

mapper.delete:
delete
from keywords
where id = #{id} and domain_id = #{domainId}
